package com.day1Practice;

public class PrintOddAndEvenNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1; i<=10; i++){
			if(i%2==0){
				System.out.print(i+" ");
			}
			}
		System.out.println();
		for(int i=0; i<=10; i++){
			if(i%2!=0){
			System.out.print(i+" ");
			}
		}
		}

	}

